package mari;

public class Odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =15;
		switch(a%2) {
		case 0:
			System.out.println(" a is Even number");
			break;
		case 1:
			System.out.println(" a is odd number");
		
			break;
		
		default:
			System.out.println("a is not a number");
		}
}
}
